CREATE DATABASE poketrash CHARACTER SET 'utf8';

USE poketrash;

CREATE TABLE `pokedex` (
  `id` SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
  `pkmName` VARCHAR(255) NOT NULL,
  `type1` VARCHAR(255),
  `type2` VARCHAR(255),
  `evolution1` SMALLINT,
  `evolution2` SMALLINT,
  `bio` MEDIUMTEXT,
  `img` VARCHAR(255),
  PRIMARY KEY (Id)
);

CREATE TABLE `users` (
  `id` SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
  `username` VARCHAR(255) NOT NULL,
  `password` VARCHAR(255) NOT NULL,
  `email` VARCHAR(255) NOT NULL,
  PRIMARY KEY (Id)
);

CREATE TABLE `profile` (
  `avatar` VARCHAR(255),
  `favorite` SMALLINT,
  `bio` MEDIUMTEXT,
  `id` SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (Id)
);

CREATE Table `stats` (
  pkm_id int(11) NOT NULL,
  hp int(11) DEFAULT NULL,
  atk int(11) DEFAULT NULL,
  def int(11) DEFAULT NULL,
  atk_spe int(11) DEFAULT NULL,
  def_spe int(11) DEFAULT NULL,
  speed int(11) DEFAULT NULL,
  PRIMARY KEY (pkm_id)
);

